import java.util.ArrayList;
import java.util.Scanner;

//Ozellik hataso yapılacak
public class quiz3 {
    static ArrayList<EnvanterKaydi> envanterArray;

    public static void main(String[] args) {
        envanterArray = new ArrayList<EnvanterKaydi>();
        Scanner scan = null;
        Scanner scanner = new Scanner(System.in);

        while (true) {
            System.out.println("1. Esya Ekle");
            System.out.println("2. Esya Cikar");
            System.out.println("3. Stok Güncelle");
            System.out.println("4. Envanteri Listele");
            System.out.println("5. Dosyaya Kaydet");
            System.out.println("6. Dosyadan Yükle");
            System.out.println("7. Cikis");
            System.out.print("Seçiminizi girin: ");

            int secim = scanner.nextInt();
            scanner.nextLine();

            try {
                File envanterFile = new File("envanter.txt");
                if (!envanterFile.exists()) {
                    throw new DosyaBulunamadiException();
                }
                if (envanterFile.length() == 0) {
                    throw new BosDosyaException();
                }
                scan = new Scanner(new FileInputStream("envanter.txt"));

                switch (secim) {
                    case 1:
                        System.out.print("Esya Adi: ");
                        String ekleAd = scanner.nextLine();
                        System.out.print("Stok Miktari: ");
                        int ekleStok = scanner.nextInt();
                        if (ekleStok < 0) {
                            throw new OzellikHatasiException();
                        }
                        esyaEkle(ekleAd, ekleStok);
                        break;
                    case 2:
                        System.out.print("Silinecek esya adi: ");
                        String silAd = scanner.nextLine();
                        esyaCikarma(silAd);
                        break;
                    case 3:
                        System.out.print("Esya Adi: ");
                        String guncelleAd = scanner.nextLine();
                        System.out.print("Stok Degisimi: ");
                        int guncelleMiktar = scanner.nextInt();
                        stokGuncelleme(guncelleAd, guncelleMiktar);
                        break;
                    case 4:
                        listele();
                        break;
                    case 5:
                        dosyayaKaydet();
                        break;
                    case 6:
                        // dosyadanYukle();
                        ArrayList<EnvanterKaydi> temp = new ArrayList<EnvanterKaydi>();
                        while (scan.hasNext()) {
                            String line = scan.nextLine();
                            String[] arr = line.split(" ");
                            temp.add(new EnvanterKaydi(arr[0], Integer.parseInt(arr[1])));
                        }
                        envanterArray = temp;

                        break;
                    case 7:
                        System.out.println("Exiting");
                        return;
                }
            } catch (DosyaBulunamadiException e) {
                System.err.println("HATA: envanter dosyasi bulunamadi.");
            } catch (BosDosyaException e) {
                System.err.println("HATA: envanter dosyasi bos.");
            } catch (OzellikHatasiException e) {
                System.err.println("HATA: Esya bilgileri hatali veya eksik.");
            } catch (Exception e) {

            } finally {
                // scan.close();
                // scanner.close();
            }
        }

        // EnvanterKaydi e = new EnvanterKaydi("a", 1);
        // envanterArray.add(e);
        // esyaCikarma("b");

    }

    static void dosyayaKaydet() throws DosyaBulunamadiException {
        PrintWriter out = null;
        try {
            out = new PrintWriter(new FileOutputStream("envanter.txt"));
        } catch (Exception e) {
            out.close();
            throw new DosyaBulunamadiException();
        }
        for (int i = 0; i < envanterArray.size(); i++) {
            out.println(envanterArray.get(i).getName() + " " + envanterArray.get(i).getStok());
        }
        out.close();

    }

    static void esyaEkle(String isim, int stok) {
        boolean flag = false;
        for (int i = 0; i < envanterArray.size(); i++) {
            if (envanterArray.get(i).getName().equals(isim)) {
                int cur = envanterArray.get(i).getStok();
                envanterArray.get(i).stokGuncelle(stok - cur);
                flag = true;
                break;
            }
        }
        if (!flag) {
            envanterArray.add(new EnvanterKaydi(isim, stok));
        }
    }

    static void esyaCikarma(String isim) {
        boolean flag = false;
        for (int i = 0; i < envanterArray.size(); i++) {
            if (envanterArray.get(i).getName().equals(isim)) {
                envanterArray.remove(i);
                flag = true;
                break;
            }
        }
        try {
            if (!flag) {
                throw new EsyaYokException();
            }
        } catch (EsyaYokException e) {
            System.err.println("HATA: Istenen esya envanterde mevcut degil.");
        }
    }

    static void stokGuncelleme(String isim, int stok) {
        boolean flag = false;
        for (int i = 0; i < envanterArray.size(); i++) {
            if (envanterArray.get(i).getName().equals(isim)) {
                int cur = envanterArray.get(i).getStok();
                envanterArray.get(i).stokGuncelle(stok - cur);
                flag = true;
                break;
            }
        }
        try {
            if (!flag) {
                throw new EsyaYokException();
            }
        } catch (EsyaYokException e) {
            System.err.println("HATA: İstenen esya envanterde mevcut değil.");
        }
    }

    static void listele() {
        for (int i = 0; i < envanterArray.size(); i++) {
            System.out.println(envanterArray.get(i));
        }
    }
}

