package quiz3;

import java.io.BufferedReader;
import java.io.File;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.List;

class Envanter {
    private List<EnvanterKaydi> kayitlar = new ArrayList<>();

    public void esyaEkle(String isim, int stok) {
        for (int i = 0; i < kayitlar.size(); i++) {
            if (kayitlar.get(i).getIsim().equals(isim)) {
                try {
                    kayitlar.get(i).stokGuncelle(stok);
                } catch (StokNegatifException e) {
                    System.err.println(e.getMessage());
                }
                return;
            }
        }
        kayitlar.add(new EnvanterKaydi(isim, stok));
    }

    public void esyaCikar(String isim) throws EsyaYokException {
        for (int i = 0; i < kayitlar.size(); i++) {
            if (kayitlar.get(i).getIsim().equals(isim)) {
                kayitlar.remove(i);
                return;
            }
        }
        throw new EsyaYokException();
    }

    public void stokGuncelle(String isim, int miktar) throws EsyaYokException, StokNegatifException {
        for (int i = 0; i < kayitlar.size(); i++) {
            if (kayitlar.get(i).getIsim().equals(isim)) {
                kayitlar.get(i).stokGuncelle(miktar);
                return;
            }
        }
        throw new EsyaYokException();
    }

    public void envanterListele() {
        for (int i = 0; i < kayitlar.size(); i++) {
            System.out.println(kayitlar.get(i).getIsim() + " - Stok: " + kayitlar.get(i).getStok());
        }
    }

    public void dosyayaKaydet(String dosyaAdi) throws IOException {
        try (PrintWriter writer = new PrintWriter(new FileWriter(dosyaAdi))) {
            for (EnvanterKaydi kayit : kayitlar) {
                writer.println(kayit.getIsim() + "," + kayit.getStok());
            }
        }
    }
    public void dosyadanYukle(String dosyaAdi) throws IOException, BosDosyaException, DosyaBulunamadiException {
        File dosya = new File(dosyaAdi);
        if (!dosya.exists()) {
            throw new DosyaBulunamadiException();
        }
        try (BufferedReader reader = new BufferedReader(new FileReader(dosya))) {
            String satir;
            kayitlar.clear();
            while ((satir = reader.readLine()) != null) {
                String[] veri = satir.split(",");
                if (veri.length != 2) continue;
                kayitlar.add(new EnvanterKaydi(veri[0], Integer.parseInt(veri[1])));
            }
        }
        if (kayitlar.isEmpty()) {
            throw new BosDosyaException();
        }
    }
}

