			if (isOdd(element)) {
				oddNumbers.add(element);
			}
		}
		return oddNumbers;
	}

	@SuppressWarnings("unchecked")
	public T sumOfOdds() {
		double sum = 0;
		for (T element : list) {
			if (isOdd(element)) {
				if (element instanceof Integer) {
					sum += (Integer) element;
				} else if (element instanceof Long) {
					sum += (Long) element;
				} else if (element instanceof Float) {
					sum += (Float) element;
				} else if (element instanceof Double) {
					sum += (Double) element;
				}
